import React, { useState } from "react";
const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: true },
  { id: 3, description: "Charger", quantity: 12, packed: false },
];
function App() {
  const [items, setItems] = useState([]);

  const handleAddItem = (item) => {
    /* In React, we never want to mutate state directly.
     Instead, we want to create a new copy of the state,
     and then update the state with the new copy.
    setItems((items) => [...items, item]); */
    setItems((items) => [...items, item]);
  };

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItem} />
      <PackingList items={items} />
      <Stats />
    </div>
  );
}

export default App;

const Logo = () => {
  return <h1>🌴 Far Away 💼</h1>;
};

const Form = ({ onAddItems }) => {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({
      description,
      quantity,
    });

    if (!description) return;
    const newItem = {
      id: Math.random(),
      description,
      quantity,
      packed: false,
    };
    console.log(newItem);

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
  };

  return (
    <form className="add-form">
      <h3>What do you need for your 😍 trip?</h3>
      <select
        value={quantity}
        onChange={(event) => setQuantity(Number(event.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((number) => (
          <option key={number} value={number}>
            {number}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      />
      <button type="submit" onClick={handleSubmit}>
        Add
      </button>
    </form>
  );
};

const PackingList = ({ items }) => {
  return (
    <div className="list">
      <ul className="">
        {items.map((item) => (
          <Item key={item.id} item={item} />
        ))}
      </ul>
    </div>
  );
};

const Item = ({ item }) => {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.description} ({item.quantity})
      </span>
      <button>
        <span role="img" aria-label="Remove this item from the list">
          ❌
        </span>
      </button>
    </li>
  );
};

const Stats = () => {
  return (
    <footer className="stats">
      <em>
        💼 You have X items on your list, and you already packed X (X%) items.
      </em>
    </footer>
  );
};
