import React, { useState } from "react";
import Logo from "./Logo";
function App() {
  const [items, setItems] = useState([]);
  const handleAddItem = (item) => {
    /* In React, we never want to mutate state directly.
     Instead, we want to create a new copy of the state,
     and then update the state with the new copy.
    setItems((items) => [...items, item]); */
    setItems((items) => [...items, item]);
  };

  const handleRemoveItem = (id) => {
    setItems((items) => items.filter((item) => item.id !== id));
  };

  const handleUpdateItem = (id) => {
    setItems((items) => {
      const updatedItems = items.map((item) => {
        if (item.id === id) {
          return {
            ...item,
            packed: !item.packed,
          };
        }
        return item;
      });
      return updatedItems;
    });
  };

  const handleClearList = () => {
    const confirmed = window.confirm(
      "Are you sure you want to clear the list?"
    );
    if (confirmed) {
      setItems([]);
    }
  };

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItem} />
      <PackingList
        items={items}
        onRemoveItem={handleRemoveItem}
        onUpdateItem={handleUpdateItem}
        onClearList={handleClearList}
      />
      <Stats items={items} />
    </div>
  );
}

export default App;

const Form = ({ onAddItems }) => {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({
      description,
      quantity,
    });

    if (!description) return;
    const newItem = {
      id: Math.random(),
      description,
      quantity,
      packed: false,
    };
    console.log(newItem);

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
  };

  return (
    <form className="add-form">
      <h3>What do you need for your 😍 trip?</h3>
      <select
        value={quantity}
        onChange={(event) => setQuantity(Number(event.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((number) => (
          <option key={number} value={number}>
            {number}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      />
      <button type="submit" onClick={handleSubmit}>
        Add
      </button>
    </form>
  );
};

const PackingList = ({ items, onRemoveItem, onUpdateItem, onClearList }) => {
  const [sortBy, setSortBy] = useState("input");

  let sortedItems = [...items];

  if (sortBy === "input") {
    sortedItems = items;
  }
  if (sortBy === "description") {
    //slice() to make a copy of the array and not mutate the original array
    // because sort() mutates the original array
    sortedItems = items.slice().sort((a, b) => {
      return a.description.localeCompare(b.description);
    });
  }
  if (sortBy === "packed") {
    sortedItems = items.slice().sort((a, b) => {
      return Number(a.packed) - Number(b.packed);
    });
  }

  return (
    <div className="list">
      <ul className="">
        {sortedItems.map((item) => (
          <Item
            key={item.id}
            item={item}
            onRemoveItem={onRemoveItem}
            onUpdateItem={onUpdateItem}
          />
        ))}
      </ul>
      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort by input order</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed status</option>
        </select>
        <button className="sort" onClick={onClearList}>
          Clear List
        </button>
      </div>
    </div>
  );
};

const Item = ({ item, onRemoveItem, onUpdateItem }) => {
  return (
    <li>
      <input
        type="checkbox"
        checked={item.packed}
        onChange={() => {
          onUpdateItem(item.id);
        }}
      />
      <span
        style={{
          textDecoration: item.packed ? "line-through" : "none",
        }}
      >
        {item.quantity}x {item.description}
      </span>
      <button
        className="remove"
        onClick={() => {
          onRemoveItem(item.id);
        }}
      >
        <span role="img" aria-label="Remove this item from the list">
          ❌
        </span>
      </button>
    </li>
  );
};

const Stats = ({ items }) => {
  if (!items.length) {
    return <p className="stats">"You haven't packed anything yet! 😱"</p>;
  }

  const numItems = items.length;
  const numPackedItems = items.filter((item) => item.packed).length;
  const percentage = Math.round((numPackedItems / numItems) * 100);

  let message;
  if (percentage === 100) {
    message = "You are ready to go! 🥳";
  } else if (percentage > 50) {
    message = "You are almost ready! 😎";
  } else if (percentage > 0) {
    message = "You are getting there! 😅";
  }

  return (
    <footer className="stats">
      <em>
        {message} 💼 You have
        <b> {numItems} </b>
        items on your list, and you already packed
        <b> {numPackedItems} </b>(<b> {percentage}% </b>) items.
      </em>
    </footer>
  );
};
